mapper.update:
update t_user
user_name=#{userName}, user_sex=#{userSex}, project_team=#{projectTeam}, user_position=#{userPosition}, leader=#{leader}, role=#{role}, password=#{password}
WHERE user_id=#{userId}

mapper.select:
SELECT user_name
FROM t_user
WHERE
    user_name=#{userName}
AND
    password=#{password}
